declare module 'currency-converter-lt' {
	class CurrencyConverter {
		declare currencyFrom: string;
		declare currencyTo: string;
		declare currencyAmount: number;
		declare convertedValue: number;
		declare isDecimalComma: boolean;
		declare isRatesCaching: boolean;
		declare ratesCacheDuration: number;
		declare ratesCache: {};

		constructor(params?: { from: CurrencyCode; to: CurrencyCode; amount?: number; isDecimalComma?: boolean });

		declare currencyCode: typeof currencyCodes;
		declare currencies: { [key: CurrencyCode]: string };

		from(currencyFrom: CurrencyCode): CurrencyConverter;
		to(currencyTo: CurrencyCode): CurrencyConverter;
		amount(currencyAmount: number): CurrencyConverter;
		setDecimalComma(boolean): CurrencyConverter;
		replaceAll(text: string, queryString: string, replaceString: string): string;
		rates(): Promise<number>;
		convert(currencyAmount: number): Promise<number>;
		currencyName(currencyCode_: CurrencyCode): string;
	}

	export default CurrencyConverter;
}

